'        apex_json.write(''MONTO_BRUTO'',x.fmonto); ',
'        apex_json.write(''MONTO_DEUDA'',x.fdeuda); ',
'',
'',
'        apex_json.close_object;',
'        l_placeholders := apex_json.get_clob_output;',
