'                start with "CUENTA_CONTROL" is null',
'                connect by prior "ID" = "CUENTA_CONTROL"',
'                order siblings by "CUENTA_CONTABLE")',
'  loop ',
'    saldo:=0.00;  ',
'    flag:=0;',
'      if x.level=1 then',
'        htp.p(''<table><tr><td><span class="ccontrol">(''||htf.escape_sc(x.micontrol)|| '')</span></tr></td></table>'');',
'      end if;      ',
'    for i in (select a.id mid, a.fecha edfecha, b.descripcion||''-''||substr(a.concepto,50) documento, ',
'                  nvl(c.debito,0) dbito, nvl(c.credito,0) cdito',
'           from entradas_diario a, documentos_ediario b, dentradas_diario c  ',
'             where a.id > 0',
'               and a.fecha between fechai and fechaf',
'               and b.id=a.docid',
'               and c.edid=a.id ',
'               and c.id > 0',
'               and c.ctacot = x.cid)',
'',
'    loop',
'      flag:=1;',
'      if flag = 1 then',
'      htp.p(''<table><tr><td>-<span class="ccuenta">''||htf.escape_sc(x.micontrol)|| ''</span></tr></td></table>',
'              <table style="font-family:verdana;font-size:14px;"><tr><th><b>FECHA</b></th><th><b>DETALLE</b></th>',
'            <th><b>DEBITOS</b></th><th><b>CREDITOS</b></th><th><b>SALDO</b></th>'');',
'      end if;',
'      if x.ori = ''D'' then',
'        saldo:=(saldo + i.dbito) - i.cdito;',
'      else',
'        saldo:=(saldo + i.cdito) - i.dbito;',
'      end if;',
'',
'      htp.p(''<tr><td>''||htf.escape_sc(i.edfecha)||''</td>',
'                      <td>''||htf.escape_sc(i.documento)||''</td>',
'                      <td>''||htf.escape_sc(TO_CHAR(i.dbito,''999,999,999.90''))||''</td>',
'                      <td>''||htf.escape_sc(TO_CHAR(i.cdito,''999,999,999.90''))||''</td>',
'                      <td>''||TO_CHAR(saldo,''999,999,999.90'')||''</td></tr>'');',
'                   --    </tr><tr><td>&nbsp;</td></tr>'');',
' --INFO RECIBO, TRAMITE Y CLIENTE',
'    end loop;',
'      ',
'   end loop; ',
'   htp.p(''</table></div>'');     ',
'END;'))
