'    end if;',
'    ',
'    sys.htp.p(''    </div>'');                                                                                            -- wrapper close',
'    sys.htp.p(''</div>'');                                                                                                -- container close',
'    ',
'    ',
'    apex_json.initialize_clob_output;',
'    apex_json.open_object;',
'    apex_json.write(''ajaxId''           , l_ajax_id          );',
'    apex_json.write(''name''             , l_name             );',
