'as',
'    -- error handling',
'    l_apex_error                    apex_error.t_error;',
'    l_result                        apex_error.t_error_result;',
'    l_message                       varchar2(32000);',
'',
'    l_name                          p_item.name%type                := apex_escape.html(p_item.name);',
